INSERT INTO Language_en_US (
  Tag, Text)
SELECT 'TXT_KEY_BUILDING_CHANCERY_STRATEGY', 'Increases Diplomatic Unit production speed and increases the amount of Influence received for Diplomatic Units built in the city. The Civil Servant specialist of this building offers a little [ICON_CULTURE] Culture, [ICON_RESEARCH] Science and [ICON_GOLD] Gold, in exchange for [ICON_FOOD] Food. The additional yields from City-State Friends and Allies increases the value of positive City-State relations during the mid-game.';

