int CvDiplomacyAI::GetLandDisputeLevelScore(PlayerTypes ePlayer)
{
	int iOpinionWeight = 0;
	int iEra = (int)GetPlayer()->GetCurrentEra();
	if (iEra <= 0)
		iEra = 1;

	switch (GetLandDisputeLevel(ePlayer))
	{
	case DISPUTE_LEVEL_FIERCE:
		iOpinionWeight += /*40*/ GD_INT_GET(OPINION_WEIGHT_LAND_FIERCE);
		break;
	case DISPUTE_LEVEL_STRONG:
		iOpinionWeight += /*25*/ GD_INT_GET(OPINION_WEIGHT_LAND_STRONG);
		break;
	case DISPUTE_LEVEL_WEAK:
		iOpinionWeight += /*15*/ GD_INT_GET(OPINION_WEIGHT_LAND_WEAK);
		break;
	case DISPUTE_LEVEL_NONE:
		iOpinionWeight += /*-10*/ GD_INT_GET(OPINION_WEIGHT_LAND_NONE);
		break;
	}
	
	// AI will care more about land if they're a warmonger or it's the early game.
	if (iOpinionWeight > 0)
	{
		if (IsConqueror())
		{
			iOpinionWeight += /*10*/ GD_INT_GET(OPINION_WEIGHT_LAND_WARMONGER);
		}
	}
	else if (iOpinionWeight < 0)
	{
		if (IsConqueror())
		{
			iOpinionWeight += (/*-5*/ GD_INT_GET(OPINION_WEIGHT_LAND_NONE_WARMONGER) - GetNeediness()) * iEra / 2;
		}
	}

	// Scale based on flavors and difficulty level
	if (iOpinionWeight != 0)
	{
		iOpinionWeight *= GetBoldness();

		if (iOpinionWeight > 0)
		{
			iOpinionWeight *= GET_PLAYER(ePlayer).isHuman() ? GET_PLAYER(ePlayer).getHandicapInfo().getAIDeclareWarProb() : GC.getGame().getHandicapInfo().getAIDeclareWarProb();
			iOpinionWeight /= 1500;
		}
		else
		{
			if (GET_PLAYER(ePlayer).isHuman() && GET_PLAYER(ePlayer).getHandicapInfo().getAIDeclareWarProb() > 200)
			{
				iOpinionWeight *= GET_PLAYER(ePlayer).getHandicapInfo().getAIDeclareWarProb();
				iOpinionWeight /= 1000;
			}
			else if (!GET_PLAYER(ePlayer).isHuman() && GC.getGame().getHandicapInfo().getAIDeclareWarProb() > 200)
			{
				iOpinionWeight *= GC.getGame().getHandicapInfo().getAIDeclareWarProb();
				iOpinionWeight /= 1000;
			}
			else
			{
				iOpinionWeight /= 5;
			}
		}
	}
	
	return iOpinionWeight;
}

