int CvDiplomacyAI::GetDifferenceFromAverageFlavorValue(int iValue) const
{
	int iDefaultFlavorValue = /*5*/ GC.getGame().GetDefaultFlavorValue();
	return iValue - iDefaultFlavorValue;
}


